import { AxiosResponse } from "axios";

type ConstructorOptions = {
   timeout?: number,
   hostname?: string,
   pathname?: string,
}

type EditOptions = {
   variety?: string,
   autoApply?: boolean,
}

declare class Client {
  constructor(apiKey: string, options?: ConstructorOptions);

  edits(text: string, sessionId: string, options?: EditOptions):  Promise<AxiosResponse>;
  accept(editUUID: string, sessionId: string): void;
  reject(editUUID: string, sessionId: string): void;
}
